import React, { useEffect, useState } from 'react';

const TaskCreate = ({ fetchData }) => {

    // TODO add project to Git

    const [newTask, setNewTask] = useState(null);
    const [inputValue, setInputValue] = useState(null);
    const [go, setGo] = useState('');

    const addTaskUrl = "//localhost:2000/addTask"

    function addNewTask (event) {
        // if (event.charCode === 13 || event.key === "Enter") {
        setNewTask(event.target.value);
    }

    useEffect(() => {
        const submitTaskAndFetchData = async () => {
            if (newTask != null) {
                let formData = new FormData();
                formData.append('title', newTask);
                let req = {
                    method: 'POST',
                    body: formData,
                }
                let resp = await fetch(addTaskUrl, req);
                if (resp.status < 400) {
                    console.log('successfully added');
                    setNewTask('');
                    setInputValue('');

                    fetchData();
                } else {
                    console.log('failed to add');
                }
            } else {
                fetchData();
            };
        }

        submitTaskAndFetchData();
    }, [])

    // async function submitNewTask (event) {
