import React, { useState } from 'react';

const TaskCreate = () => {

    const [newTask, setNewTask] = useState(null);

    const newTaskUrl = "//localhost:2000/addTask/"

    async function addNewTask (event) {
        // if (event.charCode === 13 || event.key === "Enter") {
            let task = event.target.value;
            setNewTask(task);
    }

    async function submitNewTask (event) {
        let req = {
            method: 'POST',
            // TODO how to match up req body and backend expectations
            // use FormData
            headers: { 'content-type': 'multipart/form-data'},
            body: newTask
        };
        let resp = await fetch(newTaskUrl, req);
        if (resp.status < 400) {
            console.log('successfully added')
        } else {
            console.log('failed to add');
        }
    }

      return (
        <div className='task-create ba b--light-gray'>
            <form
                // TODO onSubmit={ new method for backend request }
            >
                <label for='newtask'>Add a task:</label>
                <input 
                    type='text'
                    onChange= {(event) => addNewTask(event)}
                />
                <button type="submit">Submit</button>
            </form>
        </div>
      );

  }

export default TaskCreate;
