import React, { useState } from 'react';

const TaskCreate = ({ fetchData }) => {

    const [newTask, setNewTask] = useState(null);
    const [inputField, setInputField] = useState(null);

    const newTaskUrl = "//localhost:2000/addTask"

    async function addNewTask (event) {
        // if (event.charCode === 13 || event.key === "Enter") {
        console.log('Before setNewTask: {newTask}');
        setNewTask(event.target.value);
        console.log('After setNewTask: {newTask}');

    }

    async function clearInput (value) {
        setInputField(null);
    }

    async function submitNewTask (event) {
        // console.log(inputField);
        let formData = new FormData();
        formData.append('title', newTask);
        let req = {
            method: 'POST',
            body: formData,
        }
        let resp = await fetch(newTaskUrl, req);
        if (resp.status < 400) {
            console.log('successfully added')
            //setInputField(null);
                // setting ^ to '' clears it once, but then you can't type in it again
                // setting ^ to null doesn't clear the field
            // setInputField(null);
            setNewTask(null);
            fetchData();
        } else {
            console.log('failed to add');
        }
    }

    // TODO add project to Git

      return (
        <div className='task-create ba b--light-gray'>
                {/* <label>
                    Add a task:
                </label> */}
                <input className='w-30'
                    type='text'
                    placeholder='What do you need to do?'
                    // value={ newTask }
                    onChange= {(event) => addNewTask(event)}
                    autoFocus='true'
                />
                <button
                    type='submit'
                    // onClick= {(event) => { submitNewTask(event); clearInput() }}
                    onClick= {(event) => submitNewTask(event)}
                >
                    Submit
                </button>
        </div>
      );

  }

export default TaskCreate;
