import React from 'react';
import { icon, library } from '@fortawesome/fontawesome-svg-core'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { fas } from '@fortawesome/free-solid-svg-icons'
import { Tachyons } from 'tachyons'
import TaskCreate from './TaskCreate';

library.add(fas)

const ExistingTasks = ({ existingTasks, deleteTask, completeTask, loading, isActive }) => {

    if (loading) {
        return 'xyz';
    }

    if (existingTasks && existingTasks.length > 0) {
        return (
            <div>
                <div className='existing-tasks'>
                    {existingTasks.map((task) => {
                        let completionIcon = (
                            task.status === "completed" 
                            ? <FontAwesomeIcon icon="check-square" />
                            : <FontAwesomeIcon icon="check" />
                        )
                        return (
                            <div className='task-parent ba b--gray'>
                                <div className={
                                    // isActive 
                                    task.status === "completed"
                                    ? 'active-title task-child w-70'
                                    : 'inactive-title task-child w-70'}
                                >
                                    { task.title }
                                </div>
                                <div className='w-20'>
                                    { task.status }
                                </div>
                                <div className='task-child w-5'>
                                    <label
                                        onClick = {() => completeTask(task.id)}
                                    >
                                        { completionIcon }
                                    </label>
                                </div>
                                <div className='delete-btn task-child w-5'>
                                    <label
                                        onClick = {() => deleteTask(task.id)}
                                    >
                                        < FontAwesomeIcon icon="trash" />
                                    </label>
                                </div>
                            </div>        
                        );
                    })}
                </div>
            </div>
        );
    }

    return null;

}

export default ExistingTasks;
