import React, { useEffect, useState } from 'react';
import { icon, library } from '@fortawesome/fontawesome-svg-core'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { fas } from '@fortawesome/free-solid-svg-icons'
import { Tachyons } from 'tachyons'
import TaskCreate from './TaskCreate';

library.add(fas)

const ExistingTasks = () => {

    const [existingTasks, setExistingTasks] = useState(null);
    const [loading, setLoading] = useState(null);
    const [error, setError] = useState(false);

    // NOTE Other methods are triggered by events; fetchData isn't and so requires useEffect to initialize the call.
    // This is for loading the page with the data before the user interacts with it.
    useEffect( () => {
        fetchData();
    }, [])

    // TODO change from static id to variable once there's login functionality
    const testUserId = 1
    const fetchUrl = "http://localhost:2000/activeTasksForUser/" + testUserId;

    async function fetchData() {
        setLoading(true);
        // TODO once you can toggle to all tasks, use that state to choose url to hit
        let req = await fetch(fetchUrl);
        if (req.status < 400) {
            let jsonData = await req.json();
            setExistingTasks(jsonData);
        } else {
            setError(true);
        }
        setLoading(false);
    }

    const deleteUrl = "//localhost:2000/deleteTask/"

    async function deleteTask(id) {
        setLoading(true);
        let url = deleteUrl + id
        let req = await fetch(url, {
            method: 'DELETE',
        });
        if (req.status < 400) {
            fetchData()
        } else {
            setError(true);
        }
        setLoading(false);
    }

    async function newTaskHandler (event) {
        if (event.key === "Enter") {
            this.setState({ inputValue: event.target.value });
            console.log('You hit enter.')
        }
    }

    if (loading) {
        return 'xyz';
    }

    if (existingTasks && existingTasks.length > 0) {
        return (
            <div>
                <div className='existing-tasks'>
                    {existingTasks.map((task) => {
                        return (
                            <div className='task-parent ba b--gray'>
                                <div className='task-title task-child w-80'>
                                    { task.title }
                                </div>
                                {/* TODO add a button to complete a task */}
                                <div className='circle-btn task-child w-10'>
                                    <label>
                                        < FontAwesomeIcon icon="check" />
                                        {/* TODO once check is clicked, change to check-circle */}
                                        {/* TODO connect with backend to update status to completed */}
                                    </label>
                                </div>
                                <div className='delete-btn task-child w-10'>
                                    <label
                                        onClick = {() => deleteTask(task.id)}
                                    >
                                        < FontAwesomeIcon icon="trash" />
                                    </label>
                                </div>
                            </div>        
                        );
                    })}
                </div>
                <div className='task-create ba b--light-gray'>
                    <form>
                        <label for='newTask'>Add a task:</label>
                        <input 
                            type='text'
                            onChange= {() => newTaskHandler(this.input)}
                        />
                    </form>
                </div>
                {/* Below ln doesn't render the div - why? */}
                {/* {TaskCreate}  */}
            </div>
        );
    }

    return null;

}

export default ExistingTasks;
