import React, { useEffect, useState } from 'react';
import { findIconDefinition, icon, library } from '@fortawesome/fontawesome-svg-core'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { fas } from '@fortawesome/free-solid-svg-icons'

library.add(fas)

const ExistingTasks = () => {
    // TODO:
    // view all existing tasks
    // deleteTask
    // editTask
        // title, collaborators
    // completeTask 

    const [existingTasks, setExistingTasks] = useState(null);
    const [loading, setLoading] = useState(null);
    const [error, setError] = useState(false);

    useEffect( () => {
        fetchData();
    }, [])

    const fetchUrl = "http://localhost:2000/allTasksForUser/1";

    async function fetchData() {
        setLoading(true);
        let req = await fetch(fetchUrl);
        if (req.status < 400) {
            let jsonData = await req.json();
            setExistingTasks(jsonData);
            console.log(existingTasks); 
            console.log(jsonData); 
        } else {
            setError(true);
        }
        setLoading(false);
    }

    const deleteUrl = "http://localhost:2000/deleteTask/"

    // async function deleteTask(id) {
    //     setLoading(true);
    //     let url = deleteUrl + id
    //     let req = await fetch(url);
    //     if (req.status < 400) {
    //         // TODO edit fetchData to only show active tasks
    //         fetchData()
    //     } else {
    //         setError(true);
    //     }
    //     setLoading(false);
    // }

    deleteTask = e => {
        const deleteId = e.target.dataset.remove;
        console.log(deleteId)
        fetchData();
    }

    if (loading) {
        return 'xyz';
    }

    if (existingTasks && existingTasks.length > 0) {
        return (
            <div className='existing-tasks'>
                {existingTasks.map((task) => {
                    return (
                        <div className='task-parent'>
                            <div className='task-title task-child'>
                                { task.title }
                            </div>
                            <div className='delete-btn task-child'>
                                <label
                                    // TODO change to call an actual delete method
                                    // use a task id to pass to the backend url
                                    onClick = {() => deleteTask()}
                                    data-remove={task.id}
                                >
                                    < FontAwesomeIcon icon="trash" />
                                </label>
                            </div>
                        </div>        
                    );
                })}
            </div>
        );
    }

    return null;

}

export default ExistingTasks;
