import React, { useEffect, useState } from 'react';
import { findIconDefinition, library } from '@fortawesome/fontawesome-svg-core'
import { fas } from '@fortawesome/free-solid-svg-icons'

library.add(fas)

const trashcan = findIconDefinition({ prefix: 'fas', iconName: 'trash' })

const ExistingTasks = () => {
    // TODO:
    // view all existing tasks
    // deleteTask
    // editTask
        // title, collaborators
    // completeTask 

    const [existingTasks, setExistingTasks] = useState(null);
    const [loading, setLoading] = useState(null);
    const [error, setError] = useState(false);

    useEffect( () => {
        fetchData();
    }, [])

    const url = "http://localhost:2000/allTasksForUser/1";

    async function fetchData() {
        setLoading(true);
        let req = await fetch(url);
        if (req.status < 400) {
            let jsonData = await req.json();
            setExistingTasks(jsonData);
            console.log(existingTasks); 
            console.log(jsonData); 
        } else {
            setError(true);
        }
        setLoading(false);
    }

    if (loading) {
        return 'xyz';
    }

    if (existingTasks && existingTasks.length > 0) {
        return (
            <div className='existingTasks'>
                {existingTasks.map((task) => {
                    return (
                        <div className='task-parent'>
                            <div className='task-title task-child'>
                                { task.title }
                            </div>
                            <div className='delete-btn task-child'>
                                <i class="fas fa-trash"></i>
                            </div>
                        </div>        
                    );
                })}
            </div>
        );
    }

    return null;

}

export default ExistingTasks;
