        setLoading(true);
        // TODO once you can toggle between task types, use state to choose url
        let req = await fetch(fetchUrl);
        if (req.status < 400) {
            let jsonData = await req.json();
            setExistingTasks(jsonData);
        } else {
            setError(true);
        }
        setLoading(false);
    }

    return (
        <div>
            <ExistingTasks />
            <TaskCreate />
        </div>
    )
}

export default TaskManager;
