import React, { useState } from 'react';

const TaskCreate = () => {

    const [newTask, setNewTask] = useState(null);

    const newTaskUrl = "//localhost:2000/addTask"

    async function addNewTask (event) {
        // if (event.charCode === 13 || event.key === "Enter") {
            // let task = event.target.value;
            setNewTask(event.target.value);
    }

    async function submitNewTask (event) {
        event.preventDefault();
        let formData = new FormData();
        // let url = `${newTaskUrl}?=${newTask}`;
        // console.log(url);
        formData.append('title', newTask);
        // formData.append('title', 'please');
        // console.log(formData.getAll['title']);
        // console.log(formData['title']);
        for (var key of formData.entries()) {
			console.log(key[0] + ', ' + key[1])
		};
        let req = {
            method: 'POST',
            body: formData,
        }
        console.log(req);
        let resp = await fetch(newTaskUrl, req);
        console.log(resp.status);
        if (resp.status < 400) {
            console.log('successfully added')
        } else {
            console.log('failed to add');
        }
    }

    // TODO add project to Git

      return (
        <div className='task-create ba b--light-gray'>
            <form
                // pass event as parameter below and in ln 15?
                    // but i don't use event
                // submitNewTask() vs () = > submit...() vs no ()
                // onSubmit= {submitNewTask()} >> adds null + every keystroke as new task
                // onSubmit= {submitNewTask} >> nothing happens
                // onSubmit= {() => submitNewTask} >> nothing happens
// event.preventDefault() plus initializing submitNewTask with event param ...
// adds task to db, but list does not get refreshed, and input remains in input box
// adding await fetch results in successful response instead of undefined response

                onSubmit= {(event) => submitNewTask(event)}
            >
                <label>Add a task:</label>
                <input 
                    type='text'
                    onChange= {(event) => addNewTask(event)}
                />
                <button type="submit">Submit</button>
            </form>
        </div>
      );

  }

export default TaskCreate;
