import React, { useState } from 'react';

const TaskCreate = ({ fetchData }) => {

    const [newTask, setNewTask] = useState(null);
    const [inputValue, setInputValue] = useState(null);

    const addTaskUrl = "//localhost:2000/addTask"

    async function addNewTask (event) {
        // if (event.charCode === 13 || event.key === "Enter") {
        setNewTask(event.target.value);
    }

    async function clearInput () {
        // setInputValue((state) => {
        //     return {state: null}
        // });
            // ^ input changes to '[object Object]'
        setInputValue(() => {
            console.log(inputValue)
            return null
        });
            // ^ input doesn't change to null; remains same as newTask
    }

    async function submitNewTask (event) {
        console.log(newTask);
        console.log(inputValue);
        let formData = new FormData();
        formData.append('title', newTask);
        let req = {
            method: 'POST',
            body: formData,
        }
        let resp = await fetch(addTaskUrl, req);
        if (resp.status < 400) {
            console.log('successfully added')
            //setInputValue(null);
                // setting ^ to '' sets field to empty string that you then can't type over
                // setting ^ to null doesn't clear the field
            // setInputValue(null);
            // setNewTask(null);
            clearInput();
            fetchData();
        } else {
            console.log('failed to add');
        }
    }

    // TODO add project to Git

      return (
        <div className='task-create ba b--light-gray'>
            {/* <label> Add a task: </label> */}
            <input 
                className='w-30'
                type='text'
                placeholder='What do you need to do?'
                value={inputValue}
                onChange= {(event) => addNewTask(event)}
                autoFocus='true'
            />
            <button
                // type='submit'
                // onClick= {(event) => { submitNewTask(event); clearInput() }}
                onClick= {(event) => submitNewTask(event)}
            >
                Submit
            </button>
        </div>
      );

  }

export default TaskCreate;
